mapper.update:
update user
set username = #{username,jdbcType=VARCHAR},
switch1 = #{switch1,jdbcType=TINYINT},
switch2 = #{switch2,jdbcType=TINYINT},
camera = #{camera,jdbcType=TINYINT}
where id = #{id,jdbcType=INTEGER}

insert INTO printer_info (id, username,switch1,switch2,camera) VALUES
(#{id,jdbcType=INTEGER}, #{username,jdbcType=VARCHAR}, #{switch1,jdbcType=INTEGER},#{switch2,jdbcType=INTEGER}, #{camera,jdbcType=INTEGER})
ON DUPLICATE KEY UPDATE username=#{username,jdbcType=VARCHAR}, switch1 = #{switch1,jdbcType=INTEGER}, switch2 = #{switch2,jdbcType=INTEGER}, camera = #{camera,jdbcType=INTEGER}

insert INTO printer_info (id,temperature,humidity) VALUES
(#{id,jdbcType=INTEGER}, #{temperature,jdbcType=FLOAT}, #{humidity,jdbcType=FLOAT})
ON DUPLICATE KEY UPDATE temperature = #{temperature,jdbcType=FLOAT}, humidity=#{humidity,jdbcType=FLOAT}
